st:sparqltree:
   {

rdfs:label ;
           a  .
       ?subject a ?item;
           a ?subjType .
       ?subjType rdfs:subClassOf ?super1 .
       ?super1 rdfs:subClassOf ?super2 .
       OPTIONAL { ?super2 rdfs:subClassOf ?super3 }
       FILTER(
           ?super1 = rinfo:Rattsinformationsdokument ||
           ?super2 = rinfo:Rattsinformationsdokument ||
           ?super3 = rinfo:Rattsinformationsdokument
       )
   } UNION {
       ?subject dc:publisher ?item .
       OPTIONAL {
           ?item rdfs:label ?symbol .
       }
   } UNION {
       ?subject ?item ?symbol .
       FILTER(datatype(?symbol) = xsd:date)
   }
   # TODO: bind when called if needed (huge speedup!)
   #FILTER(?subject = <http://rinfo.lagrummet.se/publ/01fs/1911:42>)
