st:sparqltree.org:
a foaf:Organization;
       foaf:name  .
       OPTIONAL {
           ?org skos:altLabel  .
       }
       OPTIONAL {
           ?org rdfs:comment  .
       }
       OPTIONAL {

               dct:publisher ?org;
               a ?serieType .

                   rdfs:label  .

               {
                   ?serie rdfs:label  .
               } UNION {
                   ?serie skos:prefLabel  .
               }
               OPTIONAL {
                   ?serie skos:altLabel  .
               }
               OPTIONAL {
                   ?serie rdfs:comment  .
               }

       }
       OPTIONAL {

               a awol:Feed;
               awol:id ;
               dct:publisher ?org;
               iana:current  .

       }
