st:sparqltree:
   {

rdfs:label ;
           a  .
       ?subject a ?item;
           a ?subjType .
       ?subjType rdfs:subClassOf ?super1 .
       ?super1 rdfs:subClassOf ?super2 .
       OPTIONAL { ?super2 rdfs:subClassOf ?super3 }
       FILTER(
           ?super1 = rpubl:Rattsinformationsdokument ||
           ?super2 = rpubl:Rattsinformationsdokument ||
           ?super3 = rpubl:Rattsinformationsdokument
       )
   } UNION {
       ?subject dct:publisher ?item .
       OPTIONAL {
           ?item rdfs:label ?symbol .
       }
   } UNION {
       ?subject ?item ?symbol .
       FILTER(datatype(?symbol) = xsd:date)
   }
